import { suspendType } from '@/api/workFlow/FlowBefore'
export default {
  components: {},
  props: {
  },
  data() {
    return {
      visible: false,
      eventType: '',
      dataForm: {
        suspend: false,
        handleOpinion: '',
        fileList: [],
      },
      signVisible: false,
      fromRules: {
        nodeCode: [
          { required: true, message: '请选择指派节点', trigger: 'change' }
        ]
      },
      signImg: '',
      btnLoading: false,
      title: '',
      label: '',
      suspendOptions: []
    }
  },
  computed: {
  },
  methods: {
    init(id) {
      this.visible = true
      this.dataForm.suspend = false
      this.dataForm.handleOpinion = ''
      this.dataForm.fileList = []
      this.suspendOptions = []
      this.$nextTick(() => {
        this.$refs['dataForm'].resetFields()
        suspendType(id).then(res => {
          let listOptions = []
          if (res.data) {
            listOptions = [{ value: false, label: '全部流程挂起' }, { value: true, label: '主流程挂起' }]

          } else {
            listOptions = [{ value: false, label: '全部流程挂起' }]
          }
          this.suspendOptions = listOptions
        })
      })
    },
    handleSure() {
      this.$refs['dataForm'].validate((valid) => {
        if (valid) {
          this.btnLoading = true
          this.$emit('submit', this.dataForm)
        }
      })
    },
    closeDialog() {
      this.btnLoading = false
      this.visible = false
    },
  }
}
