.drawer-container {
  font-size: 14px;
  line-height: 1.5;
  word-wrap: break-word;
  >>> .el-drawer__body {
    padding: 30px 20px;
    overflow: auto;
    overflow-x: hidden;
  }
  >>> .nav_items {
    display: flex;
    justify-content: space-between;
    flex-wrap: wrap;
    li {
      list-style: none;
      text-align: center;
      display: flex;
      flex-direction: column;
      padding-bottom: 10px;
      cursor: pointer;
      width: 65px;
      .imgItems {
        position: relative;
        .img {
          width: 65px;
          height: 55px;
          border-radius: 10px;
          overflow: hidden;
          img {
            width: 100%;
            height: 100%;
          }
        }
        .icon-checked {
          display: block;
          width: 10px;
          height: 10px;
          border: 10px solid #409eff;
          border-left: 10px solid transparent;
          border-top: 10px solid transparent;
          border-bottom-right-radius: 10px;
          position: absolute;
          transform: scale(0.8);
          right: -1px;
          bottom: -1px;
          i {
            position: absolute;
            top: -2px;
            left: -3px;
            font-size: 12px;
            color: #fff;
            transform: scale(0.8);
          }
        }
      }
      span {
        font-size: 12px;
        color: #606266;
        margin-top: 10px;
      }
    }
  }
  .drawer-title {
    margin-bottom: 15px;
    color: rgba(0, 0, 0, 0.85);
    font-size: 14px;
    line-height: 22px;
    text-align: center;
    margin-top: 24px;
    font-weight: bolder;
  }

  .drawer-item {
    color: #606266;
    font-size: 14px;
    padding: 12px 0;
  }

  .drawer-switch {
    float: right;
  }
  .colorList {
    padding: 10px 0;
    .el-tag {
      width: 24px;
      height: 24px;
      border: none !important;
      margin-right: 4px;
      border-radius: 2px;
      cursor: pointer;
      padding: 0;
      text-align: center;
      vertical-align: top;
    }
    .el-icon-check {
      color: #fff;
      font-size: 16px;
      line-height: 24px;
    }
  }
}
