import { useTextMask } from '@/components/Generator/utils/useTextMask';
export default {
  name: 'JnpfInput',
  props: {
    value: {
      type: [String, Number],
      default: ''
    },
    addonBefore: {
      type: String,
      default: ''
    },
    addonAfter: {
      type: String,
      default: ''
    },
    detailed: {
      type: Boolean,
      default: false
    },
    useMask: {
      type: Boolean,
      default: false
    },
    maskConfig: {
      type: Object,
      default: () => { }
    },
    showOverflow: {
      type: Boolean,
      default: false
    }
  },
  data() {
    return {
      innerValue: '',
      maskedValue: ''
    };
  },
  watch: {
    value: {
      handler(val) {
        this.setValue(val);
      },
      immediate: true
    },
  },
  methods: {
    setValue(value) {
      this.innerValue = value;
      if (!this.useMask) return (this.maskedValue = value);
      const { getMaskedText } = useTextMask(this.maskConfig);
      this.maskedValue = getMaskedText(value);
    },
    onChange(value) {
      this.$emit('input', value);
    }
  }
};
