.userInfo {
  height: 100%;
  overflow: hidden;
  >>> .el-tabs__nav-scroll {
    padding-top: 0 !important;
  }
}
.sign {
  padding: 20px 50px 0px 50px;
}
.add-sign {
  position: relative;
  height: 160px;
  background-color: rgb(247, 247, 247);
  border-radius: 10px;
  display: flex;
  justify-content: center;
  align-items: center;
  &.active {
    border: 1px solid #1890ff;
    box-shadow: 0 0 6px rgba(6, 58, 108, 0.26);
    color: #1890ff;
    .btn,
    .icon-checked {
      display: block;
    }
  }
  .add-button {
    position: absolute;
    display: none;
  }
  .add-icon {
    font-size: 50px;
    color: rgb(157, 158, 159);
  }
  .sign-img {
    width: 100%;
    height: 100%;
  }
}
.add-sign:hover .add-button {
  display: flex;
  width: 100%;
  height: 100%;
  border-radius: 10px;
  background-color: rgba(157, 158, 159, 0.8);

  justify-content: center;
  align-items: center;
}
.icon-checked1 {
  display: block;
  width: 20px;
  height: 20px;
  border: 20px solid #1890ff;
  border-left: 20px solid transparent;
  border-top: 20px solid transparent;
  border-bottom-right-radius: 10px;
  position: absolute;
  transform: scale(0.8);
  right: -5px;
  bottom: -5px;
  i {
    position: absolute;
    top: -4px;
    left: -4px;
    font-size: 24px;
    color: #fff;
    transform: scale(0.8);
  }
}
.sign-item {
  margin-bottom: 20px;
}
